mapper.select:
select no, if(b.deleted=1,"....삭제된 글입니다.",title) as title, if( length(text) > 50,concat(substr(text,1,60),"...(생략)"),text) as text, createDate, b.userid ,u.username,b.deleted from board b left outer join user u on b.userid = u.userid
u.deleted = 0
and no = #{searchWord}
and title like concat('%',#{searchWord},'%')
and u.username like concat('%',#{searchWord},'%')
order by createDate desc Limit #{startRecordNum},#{itemPerPage}

mapper.delete:
update board set deleted = 1
where
no = #{boardno} and userid = #{userid}

mapper.select:
select article_no, original_file_name, save_file_name,
save_folder from
file where article_no = #{no}
