mapper.select:
select no,title,text,createDate,userId,userName from notice join user using(userid)
no = #{searcWord}
title like concat('%',#{searchWord},'%')
user.username like concat('%',#{searchWord},'%')
order by no desc Limit #{startRecordNum},#{itemPerPage}

mapper.update:
update notice set
title=#{title},text=#{text},createDate=now() where no = #{no} and
userid = #{userId}

